mapper.select:
SELECT COUNT(*)
FROM BOARD
WHERE USER_NO = #{userNo}

SELECT COUNT(*) FROM POINT_HISTORY WHERE USER_NO = #{userNo}
AND POINT_NO LIKE '%' || #{searchValue} || '%'
AND POINT_CONTENT LIKE '%' || #{searchValue} || '%'

SELECT COUNT(*) FROM REPORT WHERE USER_NO = #{userNo}
AND REPORT_NO LIKE '%' || #{searchValue} || '%'
AND REPORT_CONTENT LIKE '%' || #{searchValue} || '%'

mapper.update:
UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET STATUS = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE NESTED_COMMENT
SET STATUS = 'N'
WHERE NESTED_COMMENT_NO = #{nestedCommentNo}

UPDATE NESTED_COMMENT
SET STATUS = 'Y'
WHERE NESTED_COMMENT_NO = #{nestedCommentNo}
